import React, { useContext } from 'react';
import { Navigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import AuthContext from '../contexts/AuthContext';
import '../cssComponents/Dashboard.css'; // Import du fichier CSS

const Dashboard = () => {
  const { isAuthenticated, user } = useContext(AuthContext);

  // Rediriger vers la page de connexion si l'utilisateur n'est pas authentifié
  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2>Tableau de bord</h2>
      </div>
      <div className="dashboard-welcome">Bonjour, {user.nom} !</div>
      <div className="dashboard-buttons">
        <Link to="/books">
          <button>Voir les livres</button>
        </Link>
        <Link to="/loans">
          <button>Voir mes prêts en cours</button>
        </Link>
        <Link to="/profile">
          <button>Gérer mon compte</button>
        </Link>
        {user.role === 'administrateur' && (
          <Link to="/admin">
            <button>Tableau de bord administrateur</button>
          </Link>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
