import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import loanService from '../../services/loanService';

const Loans = () => {
  const [loans, setLoans] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchLoans = async () => {
      const data = await loanService.getAllLoans();
      setLoans(data);
      setIsLoading(false);
    };

    fetchLoans();
  }, []);

  const handleDelete = async (loanId) => {
    try {
      await loanService.deleteLoan(loanId);
      setLoans(loans.filter((loan) => loan.id !== loanId));
    } catch (error) {
      console.error('Erreur lors de la suppression du prêt :', error);
    }
  };

  if (isLoading) {
    return <p>Chargement...</p>;
  }

  if (loans.length === 0) {
    return <p>Aucun prêt trouvé.</p>;
  }

  return (
    <div>
      <h2>Liste des prêts</h2>
      <table>
        <thead>
          <tr>
            <th>ID du Livre</th>
            <th>Date d'Emprunt</th>
            <th>Date de Retour</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {loans.map((loan) => (
            <tr key={loan.id}>
              <td>{loan.idLivre}</td>
              <td>{loan.dateEmprunt.split('T')[0]}</td>
              <td>{loan.dateRetour.split('T')[0]}</td>
              <td>
                <Link to={`/loans/${loan.id}`}>
                  <button>Détails</button>
                </Link>
                <Link to={`/edit-loan/${loan.id}`}>
                  <button>Modifier</button>
                </Link>
                <button onClick={() => handleDelete(loan.id)}>Supprimer</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Loans;
