import axios from 'axios';

const API_URL = 'http://localhost:3000/api/users/';

const register = async (nom, email, motDePasse, role) => {
  const response = await axios.post(`${API_URL}register`, {
    nom,
    email,
    motDePasse,
    role,
  });
  if (response.data.token) {
    localStorage.setItem('token', response.data.token);
    console.log('Token stored after register:', response.data.token);
  }
  return response.data;
};

const login = async (email, motDePasse) => {
  const response = await axios.post(`${API_URL}login`, {
    email,
    motDePasse,
  });
  if (response.data.token) {
    localStorage.setItem('token', response.data.token);
    console.log('Token stored after login:', response.data.token);
  }
  return response.data;
};

const getUser = async () => {
  const token = localStorage.getItem('token');
  console.log('Token retrieved from localStorage:', token);
  if (!token) return null;
  const response = await axios.get(`${API_URL}profile`, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return response.data;
};

const logout = () => {
  localStorage.removeItem('token');
  console.log('Token removed from localStorage');
};

const bcrypt = require('bcryptjs'); // Nécessite l'installation du module bcryptjs

const updateProfile = async (userId, profileData) => {
  const token = localStorage.getItem('token');
  if (!token) {
    throw new Error('Token not found in localStorage');
  }

  // Hacher le nouveau mot de passe avant de l'envoyer
  if (profileData.password) {
    profileData.password = await bcrypt.hash(profileData.password, 10);
  }

  try {
    const response = await axios.put(`${API_URL}${userId}`, profileData, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Erreur lors de la mise à jour du profil!', error);
    throw error;
  }
};



const authService = {
  register,
  login,
  getUser,
  logout,
  updateProfile, // Ajout de la fonction updateProfile
};

export default authService;
