import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import bookService from '../../services/bookService';
import '../../cssComponents/EditAddBook.css'; // Import du fichier CSS

const EditBook = () => {
  const [book, setBook] = useState({
    titre: '',
    auteur: '',
    anneePublication: '',
    genre: '',
    resume: '',
    disponible: true,
  });
  const [isLoading, setIsLoading] = useState(true);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBook = async () => {
      const data = await bookService.getBookById(id);
      setBook(data);
      setIsLoading(false);
    };

    fetchBook();
  }, [id]);

  const handleInputChange = (event) => {
    const { name, value, type, checked } = event.target;
    setBook((prevBook) => ({
      ...prevBook,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    await bookService.updateBook(id, book);
    navigate('/books');
  };

  if (isLoading) {
    return <p>Chargement...</p>;
  }

  return (
    <div className="edit-book-container">
      <h2 className="edit-book-header">Modifier un livre</h2>
      <form className="edit-book-form" onSubmit={handleFormSubmit}>
        <label htmlFor="titre">Titre :</label>
        <input
          type="text"
          id="titre"
          name="titre"
          value={book.titre}
          onChange={handleInputChange}
          required
        />
        <label htmlFor="auteur">Auteur :</label>
        <input
          type="text"
          id="auteur"
          name="auteur"
          value={book.auteur}
          onChange={handleInputChange}
          required
        />
        <label htmlFor="anneePublication">Année de publication :</label>
        <input
          type="number"
          id="anneePublication"
          name="anneePublication"
          value={book.anneePublication}
          onChange={handleInputChange}
          required
        />
        <label htmlFor="genre">Genre :</label>
        <input
          type="text"
          id="genre"
          name="genre"
          value={book.genre}
          onChange={handleInputChange}
          required
        />
        <label htmlFor="resume">Résumé :</label>
        <textarea
          id="resume"
          name="resume"
          value={book.resume}
          onChange={handleInputChange}
          required
        />
        <label htmlFor="disponible">Disponible :</label>
        <input
          type="checkbox"
          id="disponible"
          name="disponible"
          checked={book.disponible}
          onChange={handleInputChange}
        />
        <button type="submit">Enregistrer les modifications</button>
      </form>
      <Link to={`/book/:id`}> <button className="book-return-button">Annuler</button> </Link>
    </div>
  );
};

export default EditBook;
