import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Importer Link et useNavigate depuis react-router-dom
import AuthContext from '../contexts/AuthContext';

const Profile = () => {
  const { isAuthenticated, user, logout } = useContext(AuthContext);
  const navigate = useNavigate(); // Utiliser useNavigate pour la redirection

  // Vérifier si l'utilisateur est authentifié
  if (!isAuthenticated) {
    // Rediriger vers la page de connexion si l'utilisateur n'est pas authentifié
    navigate('/login');
    return null; // Retourner null pour ne pas afficher le reste du composant
  }

  return (
    <div>
      <h2>Profil Utilisateur</h2>
      <p>Nom d'utilisateur : {user.nom}</p>
      <p>Email : {user.email}</p>
      <p>Rôle : {user.role}</p>
      {/* Bouton pour modifier le profil */}
      <Link to="/edit-profile">
        <button>Modifier</button>
      </Link>
      {/* Bouton pour se déconnecter */}
      <button onClick={logout}>Déconnexion</button>
      {/* Bouton pour retourner au tableau de bord */}
      <Link to="/dashboard">
        <button>Retour</button>
      </Link>
      {/* Ajoutez d'autres informations du profil si nécessaire */}
    </div>
  );
};

export default Profile;
