import React, { useState } from 'react';
import bookService from '../services/bookService';

const AddBook = () => {
  const [bookData, setBookData] = useState({
    title: '',
    author: '',
    description: '',
    price: 0,
    cover: ''
  });

  const handleInputChange = (event) => {
    setBookData({
      ...bookData,
      [event.target.name]: event.target.value
    });
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    bookService.createBook(bookData)
      .then(() => {
        // Redirect the user to the books page after adding a new book
        window.location.href = '/books';
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <h2>Add a New Book</h2>
      <form onSubmit={handleFormSubmit}>
        <div>
          <label htmlFor="title">Title:</label>
          <input type="text" name="title" value={bookData.title} onChange={handleInputChange} />
        </div>
        <div>
          <label htmlFor="author">Author:</label>
          <input type="text" name="author" value={bookData.author} onChange={handleInputChange} />
        </div>
        <div>
          <label htmlFor="description">Description:</label>
          <textarea name="description" value={bookData.description} onChange={handleInputChange}></textarea>
        </div>
        <div>
          <label htmlFor="price">Price:</label>
          <input type="number" name="price" value={bookData.price} onChange={handleInputChange} />
        </div>
        <div>
          <label htmlFor="cover">Cover:</label>
          <input type="text" name="cover" value={bookData.cover} onChange={handleInputChange} />
        </div>
        <button type="submit">Add Book</button>
      </form>
    </div>
  );
};

export default AddBook;
