import axios from 'axios';

const API_URL = 'http://localhost:3000/api/users/';

const register = async (nom, email, motDePasse, role) => {
  try {
    const response = await axios.post(`${API_URL}register`, { nom, email, motDePasse, role });
    console.log('Registration response:', response.data); // Debugging
    return response.data;
  } catch (error) {
    console.error('Error during registration:', error.response ? error.response.data : error.message); // Debugging
    throw error;
  }
};

const login = async (email, motDePasse) => {
  try {
    const response = await axios.post(`${API_URL}login`, { email, motDePasse });
    console.log('Login response:', response.data); // Debugging
    if (response.data.token) {
      localStorage.setItem('token', response.data.token); // Store token
    }
    return response.data;
  } catch (error) {
    console.error('Error during login:', error.response ? error.response.data : error.message); // Debugging
    throw error;
  }
};

const getUser = async () => {
  const token = localStorage.getItem('token');
  if (!token) return null;
  try {
    const response = await axios.get(`${API_URL}profile`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    console.log('Get user response:', response.data); // Debugging
    return response.data;
  } catch (error) {
    console.error('Error fetching user:', error.response ? error.response.data : error.message); // Debugging
    return null;
  }
};

const logout = () => {
  localStorage.removeItem('token');
  console.log('User logged out'); // Debugging
};

const authService = {
  register,
  login,
  getUser,
  logout,
};

export default authService;
