import axios from 'axios';

const API_URL = '/api/loans/';

// Créer un nouveau prêt
const createLoan = async (loanData) => {
  const response = await axios.post(API_URL, loanData);
  return response.data;
};

// Récupérer tous les prêts
const getAllLoans = async () => {
  const response = await axios.get(API_URL);
  return response.data;
};

// Récupérer un prêt par son ID
const getLoanById = async (loanId) => {
  const response = await axios.get(API_URL + loanId);
  return response.data;
};

// Mettre à jour un prêt
const updateLoan = async (loanId, loanData) => {
  const response = await axios.put(API_URL + loanId, loanData);
  return response.data;
};

// Supprimer un prêt
const deleteLoan = async (loanId) => {
  await axios.delete(API_URL + loanId);
  return loanId;
};

const loanService = {
  createLoan,
  getAllLoans,
  getLoanById,
  updateLoan,
  deleteLoan,
};

export default loanService;
