import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import bookService from '../../services/bookService';

const BookDetail = () => {
  const [book, setBook] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const { id } = useParams();
  const history = useHistory();

  useEffect(() => {
    const fetchBook = async () => {
      const data = await bookService.getBookById(id);
      setBook(data);
      setIsLoading(false);
    };

    fetchBook();
  }, [id]);

  const handleEdit = () => {
    history(`/edit-book/${id}`); // Rediriger vers la page de modification avec l'ID du livre
  };

  if (isLoading) {
    return <p>Chargement...</p>;
  }

  if (!book) {
    return <p>Livre non trouvé.</p>;
  }

  const { titre, auteur, anneePublication, genre, resume, disponible } = book;

  return (
    <div>
      <h2>{titre}</h2>
      <p>Auteur : {auteur}</p>
      <p>Année de publication : {anneePublication}</p>
      <p>Genre : {genre}</p>
      <p>Résumé : {resume}</p>
      <p>Disponible : {disponible ? 'Oui' : 'Non'}</p>
      <button onClick={handleEdit}>Modifier</button>
    </div>
  );
};

export default BookDetail;
