import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import loanService from '../../services/loanService';
import '../../cssComponents/Books.css'; // Import du fichier CSS

const Books = () => {
  const [books, setBooks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const data = await bookService.getAllBooks();
        setBooks(data);
        setIsLoading(false);
      } catch (error) {
        console.error('Erreur lors de la récupération des livres :', error);
        setIsLoading(false);
      }
    };

    fetchBooks();
  }, []);

  const handleAddLoan = async (bookId) => {
    try {
      // Ajouter un prêt pour le livre spécifié par bookId
      await loanService.addLoan({ idLivre: bookId });
      // Mettre à jour l'état des livres après l'ajout du prêt
      const updatedBooks = books.map(book => {
        if (book.id === bookId) {
          return { ...book, isLoanAdded: true };
        }
        return book;
      });
      setBooks(updatedBooks);
    } catch (error) {
      console.error('Erreur lors de l\'ajout du prêt :', error);
    }
  };

  if (isLoading) {
    return <p>Chargement...</p>;
  }

  if (books.length === 0) {
    return <p>Aucun livre trouvé.</p>;
  }

  return (
    <div className="books-container">
      <h2 className="books-header">Liste des livres</h2>
      <table className="books-table">
        <thead>
          <tr>
            <th>Titre</th>
            <th>Auteur</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book.id}>
              <td data-label="Titre">{book.titre}</td>
              <td data-label="Auteur">{book.auteur}</td>
              <td data-label="Actions">
                {/* Bouton "Ajouter un prêt" pour chaque livre */}
                {!book.isLoanAdded && (
                  <button onClick={() => handleAddLoan(book.id)}>Ajouter un prêt</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Books;
