import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import bookService from '../../services/bookService';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const data = await bookService.getAllBooks();
        setBooks(data);
        setIsLoading(false);
      } catch (error) {
        console.error('Erreur lors de la récupération des livres :', error);
        setIsLoading(false);
      }
    };

    fetchBooks();
  }, []);

  const handleDelete = async (bookId) => {
    try {
      await bookService.deleteBook(bookId);
      // Après la suppression, mettre à jour la liste des livres en retirant le livre supprimé
      setBooks(books.filter((book) => book.id !== bookId));
    } catch (error) {
      console.error('Erreur lors de la suppression du livre:', error);
    }
  };

  // Vérifier le rôle de l'utilisateur (par exemple, dans le contexte)
  const userRole = 'administrateur'; // Remplacez ceci par la méthode pour obtenir le rôle de l'utilisateur

  if (isLoading) {
    return <p>Chargement...</p>;
  }

  return (
    <div>
      <Link to="/dashboard">Retour</Link> {/* Ajout du lien de retour */}
      <h2>Liste des Livres</h2>
      {userRole === 'administrateur' && ( // Afficher le bouton "Ajouter un livre" seulement pour les administrateurs
        <Link to="/books/add">
          <button>Ajouter un livre</button>
        </Link>
      )}
      <table>
        <thead>
          <tr>
            <th>Titre</th>
            <th>Auteur</th>
            <th>Genre</th>
            <th>Année de publication</th>
            <th>Actions</th> {/* Nouvelle colonne pour les actions (détails et suppression) */}
          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book.id}>
              <td>{book.titre}</td>
              <td>{book.auteur}</td>
              <td>{book.genre}</td>
              <td>{book.anneePublication}</td>
              <td>
                <Link to={`/books/${book.id}`}>
                  <button>Détails</button>
                </Link>
                {userRole === 'administrateur' && ( // Afficher le bouton "Supprimer" seulement pour les administrateurs
                  <button onClick={() => handleDelete(book.id)}>Supprimer</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Books;
