// LoanDetail.js

import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import loanService from '../../services/loanService';

const LoanDetail = () => {
  const [loan, setLoan] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const { id } = useParams();

  useEffect(() => {
    const fetchLoan = async () => {
      const data = await loanService.getLoanById(id);
      setLoan(data);
      setIsLoading(false);
    };

    fetchLoan();
  }, [id]);

  if (isLoading) {
    return <p>Chargement...</p>;
  }

  if (!loan) {
    return <p>Aucun prêt trouvé.</p>;
  }

  const { idUtilisateur, idLivre, dateEmprunt, dateRetour } = loan;

  return (
    <div>
      <h2>Détails du prêt</h2>
      <p>Utilisateur : {idUtilisateur}</p>
      <p>Livre : {idLivre}</p>
      <p>Date d'emprunt : {dateEmprunt.split('T')[0]}</p>
      <p>Date de retour : {dateRetour.split('T')[0]}</p>
      <Link to="/loans">
        <button>Retour</button>
      </Link>
    </div>
  );
};

export default LoanDetail;
