import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../contexts/AuthContext';

const Profile = () => {
  const { isAuthenticated, user, logout } = useContext(AuthContext);
  const [isEditing, setIsEditing] = useState(false);

  // Fonction pour gérer la modification du profil
  const handleEditProfile = () => {
    setIsEditing(true);
    // Vous pouvez ajouter ici la logique pour modifier les informations du profil
  };

  // Fonction pour enregistrer les modifications du profil
  const handleSaveProfile = () => {
    setIsEditing(false);
    // Vous pouvez ajouter ici la logique pour enregistrer les modifications du profil
  };

  // Vérifier si l'utilisateur est authentifié
  if (!isAuthenticated) {
    return <p>Veuillez vous connecter pour accéder à votre profil.</p>;
  }

  return (
    <div>
      <h2>Profil Utilisateur</h2>
      <p>Nom d'utilisateur : {user.nom}</p>
      <p>Email : {user.email}</p>
      <p>Rôle : {user.role}</p>
      {/* Ajoutez d'autres informations du profil si nécessaire */}
      {isEditing ? (
        <button onClick={handleSaveProfile}>Enregistrer</button>
      ) : (
        <button onClick={handleEditProfile}>Modifier</button>
      )}
      <button onClick={logout}>Déconnexion</button>
      <Link to="/dashboard">
        <button>Retour</button>
      </Link>
    </div>
  );
};

export default Profile;
