import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; 
import bookService from '../../services/bookService';

const BookDetail = () => {
  const [book, setBook] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBook = async () => {
      const data = await bookService.getBookById(id);
      setBook(data);
      setIsLoading(false);
    };

    fetchBook();
  }, [id]);

  const handleEdit = () => {
    navigate(`/edit-book/${id}`);
  };

  const handleDelete = async () => {
    try {
      await bookService.deleteBook(id);
      navigate('/books'); // Rediriger vers la page principale ou une autre page après la suppression
    } catch (error) {
      console.error('Erreur lors de la suppression du livre!', error);
    }
  };

  if (isLoading) {
    return <p>Chargement...</p>;
  }

  if (!book) {
    return <p>Livre non trouvé.</p>;
  }

  const { titre, auteur, anneePublication, genre, resume, disponible } = book;

  return (
    <div>
      <h2>{titre}</h2>
      <p>Auteur : {auteur}</p>
      <p>Année de publication : {anneePublication}</p>
      <p>Genre : {genre}</p>
      <p>Résumé : {resume}</p>
      <p>Disponible : {disponible ? 'Oui' : 'Non'}</p>
      <button onClick={handleEdit}>Modifier</button>
      <button onClick={handleDelete}>Supprimer</button> {/* Bouton de suppression */}
    </div>
  );
};

export default BookDetail;
