import React, { useContext, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Importer Link et useNavigate depuis react-router-dom
import AuthContext from '../contexts/AuthContext';
import '../cssComponents/Profile.css'; // Import du fichier CSS

const Profile = () => {
  const { isAuthenticated, user, logout } = useContext(AuthContext);
  const navigate = useNavigate(); // Utiliser useNavigate pour la redirection

  useEffect(() => {
    if (!isAuthenticated) {
      // Rediriger vers la page de connexion si l'utilisateur n'est pas authentifié
      navigate('/login');
    }
  }, [isAuthenticated, navigate]);

  // Retourner null pour ne pas afficher le reste du composant si l'utilisateur n'est pas authentifié
  if (!isAuthenticated) {
    return null;
  }

  return (
    <div className="profile-container">
      <h2 className="profile-header">Profil Utilisateur</h2>
      <div className="profile-info">
        <p>Nom d'utilisateur : {user.nom}</p>
        <p>Email : {user.email}</p>
        <p>Rôle : {user.role}</p>
      </div>
      <Link to={`/edit-profile/${user.id}`}>
        <button>Modifier</button>
      </Link>
      <button onClick={logout}>Déconnexion</button>
      <Link to="/dashboard">
        <button>Retour</button>
      </Link>
      {/* Ajoutez d'autres informations du profil si nécessaire */}
    </div>
  );
};

export default Profile;
