import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import bookService from '../../services/bookService';
import authService from '../../services/authService';
import '../../cssComponents/Books.css';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch books when the component mounts
    const fetchBooks = async () => {
      try {
        const response = await bookService.getAllBooks();
        setBooks(response);
      } catch (error) {
        console.error('Erreur lors de la récupération des livres :', error);
      }
    };

    fetchBooks();

    // Fetch user info to get the userId
    const fetchUser = async () => {
      try {
        const user = await authService.getUser();
        if (user && user.id) {
          setUserId(user.id);
        } else {
          console.error('User ID is not available');
        }
      } catch (error) {
        console.error('Erreur lors de la récupération de l\'utilisateur :', error);
      }
    };

    fetchUser();
  }, []);

  const handleAddLoan = (bookId) => {
    if (userId) {
      navigate(`/add-loan/${userId}/${bookId}`);
    } else {
      console.error('User ID is not set');
    }
  };

  return (
    <div className="books-container">
      <h2 className="books-header">Liste des livres</h2>
      <ul className="books-list">
        {books.map((book) => (
          <li key={book.id} className="book-item">
            <h3>{book.title}</h3>
            <p>{book.author}</p>
            <button onClick={() => handleAddLoan(book.id)}>Add Loan</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Books;
