import React, { useState, useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import AuthContext from '../contexts/AuthContext';
import '../App.css'; // Import du fichier CSS

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { login } = useContext(AuthContext);
  const navigate = useNavigate(); // Utilisation de useNavigate au lieu de history

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Vérification des noms de champs pour le login
      await login({ email: email, MotDePasse: password }); // Utilisation des noms de champs attendus par le backend
      navigate('/dashboard'); // Utilisation de navigate pour la navigation
    } catch (error) {
      console.error(error);
      // Afficher un message d'erreur à l'utilisateur
    }
  };

  return (
    <div>
      <div className="header">VOTRE BIBLIOTHEQUE EN LIGNE</div> {/* Header avec fond indigo */}
      <div className="container">
        <h2>Connexion</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Email :
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </label>
          <br />
          <label>
            Mot de passe :
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </label>
          <br />
          <button type="submit">Se connecter</button>
        </form>
        <p>
          Pas encore inscrit ? <Link to="/register">S'inscrire</Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
