import React, { useContext } from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import AuthContext from '../contexts/AuthContext';
import Navbar from './Navbar'; // Importer le composant Navbar
import '../cssComponents/Dashboard.css'; // Import du fichier CSS

const Dashboard = () => {
  const { isAuthenticated, user } = useContext(AuthContext);

  // Rediriger vers la page de connexion si l'utilisateur n'est pas authentifié
  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  return (
    <div className="dashboard-layout">
      <Navbar />
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h2>Tableau de bord</h2>
        </div>
        <div className="dashboard-welcome">Bonjour, {user.nom} !</div>
        <Outlet /> {/* Afficher le contenu des routes enfants */}
      </div>
    </div>
  );
};

export default Dashboard;
