import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import bookService from '../../services/bookService';
import authService from '../../services/authService'; // Importez authService pour obtenir userId
import '../../cssComponents/Books.css';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [userId, setUserId] = useState(null); // État pour stocker l'userId
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const data = await bookService.getAllBooks();
        setBooks(data);
        setIsLoading(false);
      } catch (error) {
        console.error('Erreur lors de la récupération des livres :', error);
        setIsLoading(false);
      }
    };

    const fetchUser = async () => {
      try {
        const user = await authService.getUser();
        if (user && user.id) {
          setUserId(user.id);
        } else {
          console.error('User ID is not available');
        }
      } catch (error) {
        console.error('Erreur lors de la récupération de l\'utilisateur :', error);
      }
    };

    fetchBooks();
    fetchUser();
  }, []);

  // Fonction pour ajouter un prêt
  const handleAddLoan = (bookId) => {
    if (userId) {
      navigate(`/add-loan/${userId}/${bookId}`);
    } else {
      console.error('User ID is not set');
    }
  };

  const handleDelete = async (bookId) => {
    try {
      await bookService.deleteBook(bookId);
      setBooks(books.filter((book) => book.id !== bookId));
    } catch (error) {
      console.error('Erreur lors de la suppression du livre:', error);
    }
  };

  if (isLoading) {
    return <p>Chargement...</p>;
  }

  return (
    <div className="books-container">
      <Link to="/dashboard">Retour</Link>
      <h2 className="books-header">Liste des Livres</h2>
      <table className="books-table">
        <thead>
          <tr>
            <th>Titre</th>
            <th>Auteur</th>
            <th>Genre</th>
            <th>Année de publication</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book.id}>
              <td data-label="Titre">{book.titre}</td>
              <td data-label="Auteur">{book.auteur}</td>
              <td data-label="Genre">{book.genre}</td>
              <td data-label="Année de publication">{book.anneePublication}</td>
              <td data-label="Actions">
                <Link to={`/books/${book.id}`}>
                  <button>Détails</button>
                </Link>
                <button onClick={() => handleDelete(book.id)}>Supprimer</button>
                <button onClick={() => handleAddLoan(book.id)}>Ajouter un prêt</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Books;
