import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import bookService from '../services/bookService';

const AddBook = () => {
  const [bookData, setBookData] = useState({
    title: '',
    author: '',
    description: '',
    price: '',
    cover: ''
  });

  const history = useHistory();

  const handleInputChange = (e) => {
    setBookData({
      ...bookData,
      [e.target.name]: e.target.value
    });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    bookService.default.createBook(bookData) // Replace 'addBook' with 'createBook'
      .then(() => {
        history.push('/books');
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <h1>Add Book</h1>
      <form onSubmit={handleFormSubmit}>
        <div>
          <label htmlFor="title">Title:</label>
          <input type="text" name="title" value={bookData.title} onChange={handleInputChange} />
        </div>
        <div>
          <label htmlFor="author">Author:</label>
          <input type="text" name="author" value={bookData.author} onChange={handleInputChange} />
        </div>
        <div>
          <label htmlFor="description">Description:</label>
          <textarea name="description" value={bookData.description} onChange={handleInputChange}></textarea>
        </div>
        <div>
          <label htmlFor="price">Price:</label>
          <input type="number" name="price" value={bookData.price} onChange={handleInputChange} />
        </div>
        <div>
          <label htmlFor="cover">Cover URL:</label>
          <input type="text" name="cover" value={bookData.cover} onChange={handleInputChange} />
        </div>
        <button type="submit">Add Book</button>
      </form>
    </div>
  );
};

export default AddBook;
