import axios from 'axios';

const API_URL = 'http://localhost:3000/api/users/';

// Fonction pour enregistrer un nouvel utilisateur
const register = async (nom, email, motDePasse, role) => {
  const response = await axios.post(`${API_URL}register`, {
    nom,
    email,
    motDePasse,
    role,
  });
  if (response.data.token) {
    localStorage.setItem('token', response.data.token);
    console.log('Token stored after register:', response.data.token);
  }
  return response.data;
};

// Fonction pour connecter un utilisateur
const login = async (email, motDePasse) => {
  const response = await axios.post(`${API_URL}login`, {
    email,
    motDePasse,
  });
  if (response.data.token) {
    localStorage.setItem('token', response.data.token);
    localStorage.setItem('userId', response.data.userId); // Stocke l'ID de l'utilisateur après la connexion
    console.log('Token stored after login:', response.data.token);
    console.log('User ID stored after login:', response.data.userId);
  }
  return response.data;
};

// Fonction pour récupérer les informations de l'utilisateur
const getUser = async () => {
  const token = localStorage.getItem('token');
  console.log('Token retrieved from localStorage:', token);
  if (!token) return null;
  const response = await axios.get(`${API_URL}profile`, {
    headers: { Authorization: `Bearer ${token}` },
  });
  return response.data;
};

// Fonction pour se déconnecter
const logout = () => {
  localStorage.removeItem('token');
  console.log('Token removed from localStorage');
};

// Fonction pour mettre à jour le profil de l'utilisateur
const updateProfile = async (userId, profileData) => {
  const token = localStorage.getItem('token');
  if (!token) {
    throw new Error('Token not found in localStorage');
  }
  
  try {
    const response = await axios.put(`${API_URL}${userId}`, profileData, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Erreur lors de la mise à jour du profil!', error);
    throw error;
  }
};

// Fonction pour récupérer la liste de tous les utilisateurs
const getAllUsers = async () => {
  const token = localStorage.getItem('token');
  console.log('Token retrieved from localStorage:', token);
  if (!token) throw new Error('Token not found in localStorage');
  
  try {
    const response = await axios.get(API_URL, {
      headers: { Authorization: `Bearer ${token}` },
    });
    return response.data;
  } catch (error) {
    console.error('Error while fetching all users:', error);
    throw error;
  }
};

// Fonction pour supprimer un utilisateur
const deleteUser = async (userId) => {
  const token = localStorage.getItem('token');
  if (!token) {
    throw new Error('Token not found in localStorage');
  }

  try {
    const response = await axios.delete(`${API_URL}${userId}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Erreur lors de la suppression de l\'utilisateur :', error);
    throw error;
  }
};

// Service d'authentification exportant toutes les fonctions nécessaires
const authService = {
  register,
  login,
  getUser,
  logout,
  updateProfile,
  getAllUsers,
  deleteUser
};

export default authService;
