// components/NavBar.js

import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../contexts/AuthContext';
import '../cssComponents/NavBar.css'; // Assurez-vous de créer ce fichier CSS pour styliser la barre de navigation

const NavBar = () => {
  const { user } = useContext(AuthContext);

  return (
    <nav className="navbar">
      <Link to="/dashboard">Tableau de bord</Link>
      <Link to="/books">Voir les livres</Link>
      <Link to="/loans">Voir mes prêts en cours</Link>
      <Link to="/profile">Gérer mon compte</Link>
      {user.role === 'administrateur' && (
        <Link to="/admin">Tableau de bord administrateur</Link>
      )}
      <button onClick={() => { /* Logic for logout */ }}>Déconnexion</button>
    </nav>
  );
};

export default NavBar;
