import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import bookService from '../../services/bookService';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        // Appel à la fonction getAllBooks du service bookService
        const data = await bookService.getAllBooks();
        setBooks(data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching books:', error);
        setIsLoading(false);
      }
    };

    fetchBooks();
  }, []); // L'array vide en deuxième argument indique que cette fonction useEffect sera exécutée une seule fois après le rendu initial

  if (isLoading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>Liste des Livres</h2>
      <Link to="/books/add">
        <button>Ajouter un livre</button>
      </Link>
      <table>
        <thead>
          <tr>
            <th>Titre</th>
            <th>Auteur</th>
            <th>Genre</th>
            <th>Année de publication</th>
            <th>Détails</th>
          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book.id}>
              <td>{book.titre}</td>
              <td>{book.auteur}</td>
              <td>{book.genre}</td>
              <td>{book.anneePublication}</td>
              <td>
                <Link to={`/books/${book.id}`}>
                  <button>Details</button>
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Books;
