import axios from 'axios';

const API_URL = 'http://localhost:3000/api/books/';

// Fonction pour récupérer le token d'authentification depuis le stockage local
const getToken = () => {
  return localStorage.getItem('authToken');
};

// Fonction pour configurer les en-têtes avec le token d'authentification
const getAuthHeaders = () => {
  const token = getToken();
  return {
    headers: {
      Authorization: `Bearer ${token}`
    }
  };
};

const createBook = async (book) => {
  try {
    const token = localStorage.getItem('token'); // ou une autre méthode pour obtenir le token
    const response = await axios.post(API_URL, book, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Erreur lors de la création du livre!', error);
    throw error;
  }
};

const getAllBooks = async () => {
  try {
    const token = localStorage.getItem('token'); // ou une autre méthode pour obtenir le token
    const response = await axios.get(API_URL, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Erreur lors de la récupération des livres!', error);
    throw error;
  }
};

// Récupérer un livre par son ID
const getBookById = async (bookId) => {
  const response = await axios.get(API_URL + bookId, getAuthHeaders());
  return response.data;
};

// Mettre à jour un livre
const updateBook = async (bookId, bookData) => {
  const response = await axios.put(API_URL + bookId, bookData, getAuthHeaders());
  return response.data;
};

// Supprimer un livre
const deleteBook = async (bookId) => {
  await axios.delete(API_URL + bookId, getAuthHeaders());
  return bookId;
};

const bookService = {
  createBook,
  getAllBooks,
  getBookById,
  updateBook,
  deleteBook,
};

export default bookService;
